import styles from "./App.module.css";
import ClockLoader from "react-spinners/BounceLoader";
import { css } from "@emotion/react";
import React, { useState } from "react";

function App() {
  const [loading, setLoading] = useState(true);
  const override = css`
    display: block;
    margin: 0 auto;
    transition: all 0.4s;
  `;
  return (
    <div className={styles.app}>
      <button onClick={() => setLoading(!loading)}>click me </button>
      <ClockLoader color="red" size={100} loading={loading} css={override} />
    </div>
  );
}

export default App;
